Recipes = new Meteor.Collection('recipes');

RecipesSchema = new SimpleSchema({
  name: {
    type: String,
    label: "Name"
  },
  desc: {
    type: String,
    label: "Description"
  },
  author: {
    type: String,
    label: "Author",
    autoValue: function(){
      return this.userId
    }
  },
  createAt: Date,
  label: "Created At",
  autoValue: function(){
    return new Date()
  }
});

Recipes.attachSchema( RecipeSchema );
